import React from "react";

const ListCardCountries = ({ listCountries, eventOnClick,  setGetCountry}) => {

    const buttonList =  document.querySelectorAll("[data-country]");

    /* const handleOnClickGetCountry = (nameCountry, dataCountries) => {
        const dataCountry = dataCountries.filter(a => a.name === nameCountry);
        setGetCountry(dataCountry[0])
    }
    */

    const filterCountry = () => {
        const nameCountry = buttonList.forEach(function(item){
            item.addEventListener("click", ()=>{
               return item.getAttribute("data-country");

           })
        });
        const dataCountry = listCountries.filter(a => a.name === nameCountry);
        setGetCountry(dataCountry[0]);
    }

    /* buttonList.forEach(function(item){
       item.addEventListener("click", ()=>{
            const nameCountry = item.getAttribute("data-country");
            filterCountry(nameCountry);
       })
    }) */

    for(let i=0; i < buttonList.length; i++){
        //const nameCountry = buttonList[i].getAttribute("data-country");
        buttonList[i].addEventListener("click", filterCountry);
    }

    return (
        <div id="cardList" className="row">
            {
                listCountries.map((country, key) =>
                    <div key={key} className="col-sm-3 mt-5">
                        <div className="card text-center bg-light mb-3" style={{ width: "20rem" }}>
                            <div className="card-header"><strong>{country.name} {country.flagmini}</strong></div>
                            <div id="card-body" className="card-body">
                                <h6 className="card-title"><small>Capital: {country.capital}</small></h6>
                                <h6 className="card-title"><small>Continent: {country.continents}</small></h6>
                                {/* <button onClick={() => eventOnClick(country.name, listCountries)} type="button" className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal">
                                    More info
                                </button> */}
                                <button 
                                    data-country={country.name} 
                                    type="button" 
                                    className="btn btn-primary" 
                                    data-bs-toggle="modal" 
                                    data-bs-target="#exampleModal">
                                        More Info
                                </button>
                            </div>
                        </div>
                    </div>
                )
            }
        </div>
    )

}

export default ListCardCountries;
