import React, { useRef, useState, useEffect } from 'react';
import axios from 'axios';
import jsPDF from 'jspdf';
import autoTable from 'jspdf-autotable'
import SelectContinents from '../selectContinents/selectContienents';
import InputSearch from '../inputSearch/inputSearch';
import ModalCountry from '../modalCountry/modalCountry';
import LoadingComponent from '../loading/loading';

const Countries = () => {

    const [countries, setCountries] = useState([]);
    const [events, setEvents] = useState([]);
    const [filter, setFilter] = useState(true);
    const [getCountry, setGetCountry] = useState([]);
    const [loading, setLoading] = useState(false);

    const ref = useRef(null);

    const continents = [
        'Europe',
        'Asia',
        'North America',
        'South America',
        'Antarctica',
        'Africa',
        'Oceania'
    ].sort();

    useEffect(() => {
        const getData = async () => {
            try {
                setLoading(true);
                const data = await axios.get('https://restcountries.com/v3.1/all');
                const newArrayCountry = data.data.map(country => {
                    return {
                        name: country.name.common,
                        oficialName: country.name.official,
                        languages: country.languages,
                        population: country.population,
                        area: country.area,
                        flagmini: country.flag,
                        region: country.region,
                        subregion: country.subregion,
                        continents: country.continents,
                        capital: country.capital,
                        flagPNG: country.flags.png,
                        flagSVG: country.flags.svg,
                        maps: country.maps.googleMaps
                    }
                }).sort((a, b) => a.name > b.name ? 1 : -1);
                //setTimeout(() => {
                setLoading(false);
                setCountries(newArrayCountry);
                //}, 1000)
            } catch (er) {
                console.log(er.message)
            }
        }

        getData();

    }, []);

    const generatePDF = () => {
        const doc = new jsPDF('p', 'pt', 'a4');

        const filename = new Date();

        //const dataTable = countries.map(a => [a.name, a.region, a.subregion, a.population, a.area, a.capital]);

        /* autoTable(doc, {
            styles: {fontSize: 8},
            head: [['Country', 'Region', 'Subregion', 'Population', 'Area', 'Capital']],
            body: dataTable
        }) */

        autoTable(doc, {
            body: [
              { europe: 'Sweden', america: 'Canada', asia: 'China' },
              { europe: 'Norway', america: 'Mexico', asia: 'Japan' },
            ],
            columns: [
              { header: 'Europe', dataKey: 'europe' },
              { header: 'Asia', dataKey: 'asia' },
              { header: 'America', dataKey: 'america'}
            ],
          })

        doc.save(`${filename.toISOString()}.pdf`)
    }

    const handleSelectContinent = (e) => {
        const optionSelect = document.getElementById("selectRegion").value;
        const inputSearch = ref.current.value;

        const arrayFilter = countries.filter((a) => {
            if (optionSelect === 'Select Continent' && inputSearch !== '')
                return a.name.toLowerCase().includes(inputSearch.toLowerCase());
            if (optionSelect !== 'Select Continent' && inputSearch !== '')
                return a.continents[0] === optionSelect && a.name.toLowerCase().includes(inputSearch.toLowerCase());
            if (optionSelect === 'Select Continent' && inputSearch === '')
                return countries
            return a.continents[0] === optionSelect;
        });

        if (arrayFilter.length === 0) {
            //setFilter(false);
            setEvents([]);
        } else {
            setFilter(false);
            setEvents(arrayFilter.sort((a, b) => a.name > b.name ? 1 : -1));
        }
    }

    const handleOnChangeSearch = (e) => {
        const stringSearch = e.target.value;
        const optionSelect = document.getElementById("selectRegion").value;

        const filterCountry = countries.filter(country => {
            if (stringSearch === '' && optionSelect !== 'Select Continent')
                return country.continents[0] === optionSelect
            if (stringSearch !== '' && optionSelect === 'Select Continent')
                return country.name.toLowerCase().includes(stringSearch.toLowerCase())
            if (stringSearch !== '' && optionSelect !== 'Select Continent')
                return country.continents[0] === optionSelect && country.name.toLowerCase().includes(stringSearch.toLowerCase())
            if (stringSearch === '' && optionSelect === 'Select Continent')
                return countries
            return country.name.toLowerCase().includes(stringSearch.toLowerCase())
        });

        if (filterCountry.length === 0) {
            //setFilter(false);
            setEvents([]);
        } else {
            setFilter(false);
            setEvents(filterCountry.sort());
        }
    }

    const handleOnClickGetCountry = (nameCountry, dataCountries) => {
        const dataCountry = dataCountries.filter(a => a.name === nameCountry);
        setGetCountry(dataCountry[0])
    }

    const stateCountries = filter ? countries : events;

    return (
        <>
            <div className="row" style={{ marginTop: "20px" }}>
                <div className="col">
                    <div className="form-group">
                        <InputSearch referenc={ref} eventOnChange={handleOnChangeSearch} />
                    </div>
                </div>
                <div className="col">
                    <div className="form-group">
                        <SelectContinents contienets={continents} eventSelect={handleSelectContinent} />
                    </div>
                </div>
                <div className="col">
                    <div className="form-group">
                        <button className="btn btn-primary" type="primary" onClick={generatePDF}>
                            Generate PDF Countries
                        </button>
                    </div>
                </div>
            </div>
            {loading ? (
                <LoadingComponent />
            ) : (
                <div className="row">
                    {
                        stateCountries.map((country, key) =>
                            <div key={key} className="col-sm-3 mt-5">
                                <div className="card text-center bg-light mb-3" style={{ width: "20rem" }}>
                                    <div className="card-header"><strong>{country.name} {country.flagmini}</strong></div>
                                    <div className="card-body">
                                        <h6 className="card-title"><small>Capital: {country.capital}</small></h6>
                                        <h6 className="card-title"><small>Continent: {country.continents}</small></h6>
                                        <button onClick={() => handleOnClickGetCountry(country.name, stateCountries)} type="button" className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal">
                                            More info
                                        </button>
                                    </div>
                                </div>
                            </div>
                        )
                    }
                </div>
            )}
            <ModalCountry dataCountries={getCountry} />
        </>
    )
}

export default Countries;
