import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Countries = () => {

    const [countries, setCountries] = useState([])

    useEffect(() => {
        const getData = async () => {
            try{
                const data = await axios.get('https://restcountries.com/v3.1/all');
                setCountries(data.data);
            }catch(er){
                console.log(er.message)
            }            
        }
        getData();
    }, []);

    //console.log(countries)

    const itemsCountry = countries.map(country => {
        return{
            name: country.name.common,
            flagmini: country.flag,
            region: country.region,
            subregion: country.subregion,
            continents: country.continents,
            capital: country.capital,
            flagPNG: country.flags.png,
            flagSVG: country.flags.svg
        }
    }).sort((a, b) => a > b ? 1 : -1)

    return(
        <div className="row">
            {
                itemsCountry.map(country => 
                    <div className="col-sm-4 mt-5">
                        <div className="card" style={{width: "18rem"}}>
                        {/* {src ? <img src={src} className="card-img-top" alt="..."></img> : ''} */}
                            <div className="card-body">
                                <h5 className="card-title">{country.name}</h5>
                                {/* <p className="card-text">{description}</p>
                                <p className="card-text"><small className="text-muted">News Feed: {source_name}</small></p>
                                <p className="card-text"><small className="text-muted">Author: {author}</small></p>
                                <a href={href} className="btn btn-primary" target="_blank" rel="noreferrer">Main News</a> */}
                            </div>
                        </div>
                    </div>
                )
            }
        </div>
    )
}

export default Countries;
