import React, { useState, useEffect } from 'react';
import axios from 'axios';

const SelectContinents = ({ contienets, eventSelect }) => {
    return (
        <select style={{width: "200px"}} id='selectRegion' className="form-select" onChange={eventSelect}>
            <option defaultValue="Select Region">Select Continent</option>
            {
                contienets.map((a, k) => {
                    return <option key={k} value={a}>{a}</option>
                })
            }
        </select>
    )
}

const Countries = () => {

    const [countries, setCountries] = useState([]);
    const [filter, setFilter] = useState([]);

    const continents = [
        'Europe',
        'Asia',
        'North America',
        'South America',
        'Antarctica',
        'Africa',
        'Oceania'
    ].sort();

    useEffect(() => {
        const getData = async () => {
            try {
                const data = await axios.get('https://restcountries.com/v3.1/all');
                setCountries(data.data);
            } catch (er) {
                console.log(er.message)
            }
        }
        getData();
    }, []);

    //console.log(countries)

    const handleSelectContinent = () => {
        const optionSelect = document.getElementById("selectRegion").value;
        const arrayFilter = countries.filter((a)=> a.continents[0] === optionSelect)
        setFilter(arrayFilter.sort((a, b) => a.name.common > b.name.common ? 1 : -1));
    }

    const handleOnChangeSearch = (e) => {
        console.log('inputSearch', e.target.value);
        let stringSearch = e.target.value;
        //const countriesOnSearch = !filter.length ? countries : filter;
        const optionSelect = document.getElementById("selectRegion").value;
        console.log('selectRegion', optionSelect)
        const arraySearch = countries.filter((country) => {
            if(!stringSearch.length && optionSelect === 'Select Region') 
                return countries
            if(optionSelect !== 'Select Region') 
                return (country.continents[0] === optionSelect && country.name.common.toLowerCase().includes(stringSearch.toLowerCase())  
                //&& country.name.common.toLowerCase().includes(stringSearch.toLowerCase())
            //return country.name.common.toLowerCase().includes(stringSearch.toLowerCase()
            );
        })
        setFilter(arraySearch)
    }

    const stateCountries = !filter.length ? countries : filter;

    const itemsCountry = stateCountries.map(country => {
        return {
            name: country.name.common,
            flagmini: country.flag,
            region: country.region,
            subregion: country.subregion,
            continents: country.continents,
            capital: country.capital,
            flagPNG: country.flags.png,
            flagSVG: country.flags.svg
        }
    }).sort((a, b) => a.name > b.name ? 1 : -1);

    return (
        <>
            <div className="form-group" style={{ margin: "10px 0 20px 0" }}>
            </div>
            <div className="row">
                <div className="col">
                    <div className="form-group">
                        <input 
                            className="form-control" 
                            id="search"
                            name="search"
                            type="search" 
                            placeholder="Search"
                            onChange={handleOnChangeSearch}
                        />
                    </div>
                </div>
                <div className="col">
                    <div className="form-group">
                        <SelectContinents contienets={continents} eventSelect={handleSelectContinent} />
                    </div>
                </div>
            </div>
            <div className="row">
                {
                    itemsCountry.map((country, key) =>
                        <div key={key} className="col-sm-3 mt-5">
                            <div className="card bg-light mb-3" style={{ width: "20rem" }}>
                                {/* {src ? <img src={src} className="card-img-top" alt="..."></img> : ''} */}
                                <div className="card-header"><strong>{country.name} {country.flagmini}</strong></div>
                                <div className="card-body">
                                    <h5 className="card-title"><small>Capital: {country.capital}</small></h5>
                                    <h5 className="card-title"><small>Continent: {country.continents}</small></h5>
                                    {/* <p className="card-text">{description}</p>
                                <p className="card-text"><small className="text-muted">News Feed: {source_name}</small></p>
                                <p className="card-text"><small className="text-muted">Author: {author}</small></p>
                                <a href={href} className="btn btn-primary" target="_blank" rel="noreferrer">Main News</a> */}
                                </div>
                            </div>
                        </div>
                    )
                }
            </div>
        </>
    )
}

export default Countries;
